import React from "react";
import { Outlet } from "react-router-dom"; // Outlet will render the matched child route component
import Navigation from "./Navigation"; // Create this component for your menu

const Layout = () => {
  return (
    <div>
      <main className="min-h-screen bg-gradient-to-tr from-blue-800 to-blue-700 flex items-center justify-center relative">
        <div className="flex flex-row min-h-[90vh] w-[90%] bg-gradient-to-br from-white/70 to-white/30 rounded-lg shadow-lg backdrop-blur-md border border-white/30">
          {/* <main className="min-h-screen bg-gradient-to-tr from-blue-800 to-blue-700 flex items-center justify-center relative">
        <div className="flex flex-col min-h-[90vh] w-11/12 bg-gradient-to-br from-white/70 to-white/30 rounded-lg shadow-lg backdrop-blur-md border border-white/30"> */}
          <Navigation /> {/* Common Navigation Bar */}
          <div className="flex flex-col flex-1 p-4">
            <Outlet /> {/* This is where routed components will be rendered */}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Layout;
